				}

				let argsString = JSON.stringify(args);

				let funcString = func.toString();

				funcString = funcString.replace(/`/g, '\\`');

				return `(function(){
					var args = ${argsString};
					(${funcString})(args);
				})()`;
			} catch (error) {
				console.log(error);
			}
